import React from 'react'
import axios from 'axios'


class SingleNote extends React.Component{

    constructor(props){
        super(props);
        this.state = { title: this.props.title, content: this.props.content };
        this.noteObj = this.props.note;
        this.handleChangeTitle = this.handleChangeTitle.bind(this);
    }

    handleChangeTitle(e){
        //Save note to database
        e.preventDefault();
        this.setState({title : e.target.value});

        if(this.state.title !== ""){
            //let tags = retreiveTags();      
            console.log("Saving change...");
            this.noteObj.note_title = e.target.value;
            axios.patch('http://localhost:4000/Notes/' + this.noteObj._id, this.noteObj).then(res => console.log(res.data));
        }
    }
    handleChangeContent(e){
        //Save note to database
        e.preventDefault();
        this.setState({content : e.target.value});

        if(this.state.title !== ""){
            //let tags = retreiveTags();
            console.log("Saving change...");
            this.noteObj.note_content = e.target.value;
            axios.patch('http://localhost:4000/Notes/' + this.noteObj._id, this.noteObj).then(res => console.log(res.data));
        }
    }
    render(){
        return (
            <div>
                <h1>
                <input 
                    type="text" 
                    placeholder="Add a title!" 
                    id="title" 
                    value={this.state.title}
                    onChange={this.handleChangeTitle} />
                </h1>
                <textarea
                    placeholder="Content" 
                    id="content"
                    value={this.state.content}
                    onChange={this.handleChangeTitle}
                    rows={8}
                    cols={42} />
            </div>
        )
    }
}

export default SingleNote
