import React from 'react'
import SingleNote from './SingleNote'
import axios from 'axios'

const Note = (props) => {
    return (
        <div>
            <SingleNote title={props.note.note_title} content={props.note.note_content} note={props.note} />
        </div>
    )
}


class NoteSearchBar extends React.Component{

    constructor(props){
        super(props);
        this.state = { value: '', current_notes: [] };
        this.handleChange = this.handleChange.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
    }

    noteList(){
        return this.state.current_notes.map(function(currentNote, i){
            return <Note note={currentNote} key={i} />
        })
    }

    handleSearch(val){
        //Update searched items from database
        //if val is empty, show most recent notes
        //if val is not empty, show notes that match the search
        //make sure to parse out dangerous input and prevent XSS
        //also extract hashtags and search for them
        axios.get('http://localhost:4000/Notes/search', {withCredentials: true}, {params: {search_term: this.state.val}})
            .then(response => {
                this.setState({ current_notes: response.data });
            }).catch(function(error){
                console.log(error);
            });
    }

    handleChange(e) {
        e.preventDefault();

        let new_val = e.target.value;
        this.setState({value: new_val});
        this.handleSearch(new_val);
    }

    render(){
        return (
            <div>
                <div name="searchbar">
                    <input type="text" id="searchbar" placeholder="Search..." onChange={this.handleChange} />
                </div>
                <div name="displayed_notes" className="flexbox-container">
                    {this.noteList()}
                </div>
            </div>
        )
    }
}

export default NoteSearchBar
