import React from 'react'
import axios from 'axios'


class SingleNote extends React.Component{

    constructor(props){
        super(props);
        this.state = { title: this.props.title, content: this.props.content, id: this.props.id };
        this.noteObj = props.note;
        this.handleChangeTitle = this.handleChangeTitle.bind(this);
    }

    handleChangeTitle(e){
        //Save note to database
        e.preventDefault();

        this.setState({[e.target.id]: e.target.value});

        if(this.state.title !== ""){
            //let tags = retreiveTags();
            
            console.log("Saving change...");

            this.noteObj.note_title = this.state.title;

            axios.patch('http://localhost:4000/Notes/' + this.state.id, note).then(res => console.log(res.data));

            console.log("Saving note...");
            console.log("Title: " + note.note_title);
            console.log("Content: " + note.note_content);
            console.log("Date: " + note.note_date);
            console.log("Owner: " + note.note_owner);
            console.log("Tags: " + note.note_tags);
        }
    }

    render(){
        return (
            <div>
                <h1>
                <input 
                    type="text" 
                    placeholder="Add a title!" 
                    id="title" 
                    value={this.state.title}
                    onChange={this.handleChangeTitle} />
                </h1>
                <textarea
                    placeholder="Content" 
                    id="content"
                    value={this.state.content}
                    onChange={this.handleChangeTitle}
                    rows={8}
                    cols={42} />
            </div>
        )
    }
}

export default SingleNote
